$(function () {
            var n = 0;
            var pos = 0;
            setInterval(function () {
                n = n + 1;
                pos = -1 * 896 * n;
                $(".slider ul").animate({ "left": pos }, 300, function () {
                    if (n == 3) {
                        n = 0;
                        pos = 0;
                        $(".slider ul").css({ "left": pos });
                    }
                });
            }, 3000);
        });